                if(bruteForce[i][j+1] === false) {
                  sentinel = false
                  console.log(9)
                }
                if(bruteForce[i][j+2] === false) {
                  sentinel = false
                  console.log(10)
                }
              }

              if(j === 1) {
                if(bruteForce[i][j-1] === false) {
                  sentinel = false
                  console.log(11)
                }
                if(bruteForce[i][j+1] === false) {
                  sentinel = false
                  console.log(12)
                }
                if(bruteForce[i][j+2] === false) {
                  sentinel = false
                  console.log(13)
                }
              }

              if(j === bruteForce[i].length - 2) {
                if(bruteForce[i][j-1] === false) {
                  sentinel = false
                  console.log(14)
                }
                if(bruteForce[i][j-2] === false) {
                  sentinel = false
                  console.log(15)
                }
                if(bruteForce[i][j+1] === false) {
                  sentinel = false
                  console.log(16)
                }
              }

              if(j === bruteForce[i].length - 1) {
                if(bruteForce[i][j-1] === false) {
                  sentinel = false
                  console.log(17)
                }
                if(bruteForce[i][j-2] === false) {
                  sentinel = false
                  console.log(18)
                }
              }
              if(sentinel) {
                bruteForce[i][j] = true
                let heaps = this.calculateHeaps(bruteForce)
                let nimsum = this.calculateNimSum(heaps)
                if(nimsum === 0) {
                  //we found our move
                  console.log("row: " + i + " col: " + j)
                  console.log("Proposed nimsum: " + nimsum)
                  break;
                } else {
                  bruteForce[i][j] = false

          }
              }
      }
    }
  }


  render() {
    return (
      <IonPage>
        <IonHeader>
          <IonToolbar>
            <IonTitle>Treblecross</IonTitle>
          </IonToolbar>
        </IonHeader>
        <IonContent>


          <IonItem id='rangeSliderItem'>
            <IonRange id='rangeSlider' onIonChange={e => {this.setState({numberOfBoards: e.detail.value})}} min={1} max={5} step={1} snaps={true} color="secondary" />
          </IonItem>
          <h2 id='numBoardsLabel'>Number of Treblecross boards selected: {+ this.state.numberOfBoards}</h2>
          <IonButton id='generateBoards' onClick={() => {this.generateTrebleCrossBoard()}}>Generate Boards</IonButton>

          {this.state.boardState.map((Board, boardIndex) => <div className='board' key={boardIndex}>{
            Board.map((Square, squareIndex) =>
              <button onClick={() => {this.updateBoardState(boardIndex, squareIndex)}} key={boardIndex.toString() + "," + squareIndex.toString()} className="square">{Square ? 'X' : ''}</button>
            )
          }</div>
          )}

        </IonContent>
      </IonPage>
    );
  }
}


