import { IonContent, IonHeader, IonPage, IonTitle, IonToolbar, IonItem, IonRange, IonButton } from '@ionic/react';
import React from 'react';
import ExploreContainer from '../components/ExploreContainer';
import './Home.css';

type MyState = {
  numberOfBoards: any;
  boardState: any;
  boardSizeArray: number[]
}

type MyProps = {

}

class Home extends React.Component<MyProps,MyState> {
  constructor(props: MyProps) {
    super(props)

    this.state = {
      numberOfBoards: 1,
      boardState: [],
      boardSizeArray: []
    }
  }




  generateTrebleCrossBoard() {
    let tempBoardSizeArray = []
    for(let i = 0; i < this.state.numberOfBoards; i++) {
      //creates a board of size 1 to 14
      tempBoardSizeArray.push(Math.floor(Math.random() * Math.floor(14))+1)
    }
  }

  render() {
    return (
      <IonPage>
        <IonHeader>
          <IonToolbar>
            <IonTitle>Treblecross</IonTitle>
          </IonToolbar>
        </IonHeader>
        <IonContent>


          <IonItem>
            <IonRange onIonChange={e => {this.setState({numberOfBoards: e.detail.value})}} min={1} max={5} step={1} snaps={true} color="secondary" />
          </IonItem>
          <h2 id='numBoardsLabel'>Number of Treblecross boards selected: {+ this.state.numberOfBoards}</h2>
          <IonButton id='generateBoards'>Generate Boards</IonButton>
        </IonContent>
      </IonPage>
    );
  }
}


export default Home;
